INSERT INTO `song` VALUES (13, '等你下课', 9, '等你下课(with 杨瑞代) - 周杰伦 (Jay Chou)\r\n\r\n词：周杰伦\r\n\r\n曲：周杰伦\r\n\r\nJay：你住的 巷子里\r\n\r\n我租了一间公寓\r\n\r\n为了想与你不期而遇\r\n\r\n高中三年 我为什么\r\n\r\n为什么不好好读书\r\n\r\n没考上跟你一样的大学\r\n\r\n我找了份工作\r\n\r\n离你宿舍很近\r\n\r\n当我开始学会做蛋饼\r\n\r\n才发现你 不吃早餐\r\n\r\n喔 你又擦肩而过\r\n\r\n你耳机听什么\r\n\r\n能不能告诉我\r\n\r\n合：躺在你学校的操场看星空\r\n\r\n教室里的灯还亮着你没走\r\n\r\n记得 我写给你的情书\r\n\r\n都什么年代了\r\n\r\n到现在我还在写着\r\n\r\n总有一天总有一年会发现\r\n\r\n有人默默的陪在你的身边\r\n\r\n也许 我不该在你的世界\r\n\r\n当你收到情书\r\n\r\n也代表我已经走远\r\n\r\nGary：学校旁 的广场\r\n\r\n我在这等钟声响\r\n\r\n等你下课一起走好吗\r\n\r\nJay：弹着琴 唱你爱的歌\r\n\r\n暗恋一点都不痛苦\r\n\r\nGary：一点都不痛苦\r\n\r\nJay：痛苦的是你\r\n\r\n合：根本没看我\r\n\r\nJay：我唱这么走心\r\n\r\nGary：这么走心\r\n\r\nJay：却走不进你心里\r\n\r\nGary：进你心里\r\n\r\nJay：在人来人往\r\n\r\n合：找寻着你 守护着你\r\n\r\n不求结局\r\n\r\n合：喔\r\n\r\nGary：你又擦肩\r\n\r\n合：而过\r\n\r\nJay：我唱告白气球\r\n\r\n终于你回了头\r\n\r\n合：躺在你学校的操场看星空\r\n\r\n教室里的灯还亮着你没走\r\n\r\n记得 我写给你的情书\r\n\r\n都什么年代了\r\n\r\n到现在我还在写着\r\n\r\n总有一天总有一年会发现\r\n\r\n有人默默的陪在你的身边\r\n\r\n也许 我不该在你的世界\r\n\r\n当你收到情书\r\n\r\n也代表我已经走远', 'https://y.gtimg.cn/music/photo_new/T002R300x300M000003bSL0v4bpKAx.jpg?max_age=2592000', NULL, NULL, NULL, NULL, NULL, '港台', '周杰伦', '04：30', NULL);
INSERT INTO `song` VALUES (14, '稻香', 9, '稻香 - 周杰伦 (Jay Chou)\r\n\r\n词：周杰伦\r\n\r\n曲：周杰伦\r\n\r\n对这个世界如果你有太多的抱怨\r\n\r\n跌倒了就不敢继续往前走\r\n\r\n为什么人要这么的脆弱堕落\r\n\r\n请你打开电视看看\r\n\r\n多少人为生命在努力勇敢的走下去\r\n\r\n我们是不是该知足\r\n\r\n珍惜一切就算没有拥有\r\n\r\n还记得你说家是唯一的城堡\r\n\r\n随着稻香河流继续奔跑\r\n\r\n微微笑 小时候的梦我知道\r\n\r\n不要哭让萤火虫带着你逃跑\r\n\r\n乡间的歌谣永远的依靠\r\n\r\n回家吧 回到最初的美好\r\n\r\n不要这么容易就想放弃\r\n\r\n就像我说的\r\n\r\n追不到的梦想换个梦不就得了\r\n\r\n为自己的人生鲜艳上色\r\n\r\n先把爱涂上喜欢的颜色\r\n\r\n笑一个吧\r\n\r\n功成名就不是目的\r\n\r\n让自己快乐快乐这才叫做意义\r\n\r\n童年的纸飞机\r\n\r\n现在终于飞回我手里\r\n\r\n所谓的那快乐\r\n\r\n赤脚在田里追蜻蜓追到累了\r\n\r\n偷摘水果被蜜蜂给叮到怕了\r\n\r\n谁在偷笑呢\r\n\r\n我靠着稻草人\r\n\r\n吹着风 唱着歌 睡着了\r\n\r\n午后吉他在虫鸣中更清脆\r\n\r\n阳光洒在路上就不怕心碎\r\n\r\n珍惜一切 就算没有拥有\r\n\r\n还记得你说家是唯一的城堡\r\n\r\n随着稻香河流继续奔跑\r\n\r\n微微笑 小时候的梦我知道\r\n\r\n不要哭让萤火虫带着你逃跑\r\n\r\n乡间的歌谣永远的依靠\r\n\r\n回家吧 回到最初的美好\r\n\r\n还记得你说家是唯一的城堡\r\n\r\n随着稻香河流继续奔跑\r\n\r\n微微笑 小时候的梦我知道\r\n\r\n不要哭让萤火虫带着你逃跑\r\n\r\n乡间的歌谣永远的依靠\r\n\r\n回家吧 回到最初的美好', 'https://y.gtimg.cn/music/photo_new/T002R300x300M000002Neh8l0uciQZ.jpg?max_age=2592000', NULL, 3, NULL, NULL, NULL, '港台', '周杰伦', '03：43', NULL);
INSERT INTO `song` VALUES (15, '晴天', 9, '晴天 - 周杰伦 (Jay Chou)\r\n\r\n词：周杰伦\r\n\r\n曲：周杰伦\r\n\r\n编曲：周杰伦\r\n\r\n故事的小黄花\r\n\r\n从出生那年就飘着\r\n\r\n童年的荡秋千\r\n\r\n随记忆一直晃到现在\r\n\r\nRe So So Si Do Si La\r\n\r\nSo La Si Si Si Si La Si La So\r\n\r\n吹着前奏望着天空\r\n\r\n我想起花瓣试着掉落\r\n\r\n为你翘课的那一天\r\n\r\n花落的那一天\r\n\r\n教室的那一间\r\n\r\n我怎么看不见\r\n\r\n消失的下雨天\r\n\r\n我好想再淋一遍\r\n\r\n没想到失去的勇气我还留着\r\n\r\n好想再问一遍\r\n\r\n你会等待还是离开\r\n\r\n刮风这天我试过握着你手\r\n\r\n但偏偏雨渐渐大到我看你不见\r\n\r\n还要多久我才能在你身边\r\n\r\n等到放晴的那天也许我会比较好一点\r\n\r\n从前从前有个人爱你很久\r\n\r\n但偏偏风渐渐把距离吹得好远\r\n\r\n好不容易又能再多爱一天\r\n\r\n但故事的最后你好像还是说了拜拜\r\n\r\n为你翘课的那一天\r\n\r\n花落的那一天\r\n\r\n教室的那一间\r\n\r\n我怎么看不见\r\n\r\n消失的下雨天\r\n\r\n我好想再淋一遍\r\n\r\n没想到失去的勇气我还留着\r\n\r\n好想再问一遍\r\n\r\n你会等待还是离开\r\n\r\n刮风这天我试过握着你手\r\n\r\n但偏偏雨渐渐大到我看你不见\r\n\r\n还要多久我才能在你身边\r\n\r\n等到放晴的那天也许我会比较好一点\r\n\r\n从前从前有个人爱你很久\r\n\r\n偏偏风渐渐把距离吹得好远\r\n\r\n好不容易又能再多爱一天\r\n\r\n但故事的最后你好像还是说了拜拜\r\n\r\n刮风这天我试过握着你手\r\n\r\n但偏偏雨渐渐大到我看你不见\r\n\r\n还要多久我才能够在你身边\r\n\r\n等到放晴那天也许我会比较好一点\r\n\r\n从前从前有个人爱你很久\r\n\r\n但偏偏雨渐渐把距离吹得好远\r\n\r\n好不容易又能再多爱一天\r\n\r\n但故事的最后你好像还是说了拜', 'https://y.gtimg.cn/music/photo_new/T002R300x300M000000MkMni19ClKG.jpg?max_age=2592000', NULL, 3, NULL, NULL, NULL, '港台', '周杰伦', '04：29', NULL);
INSERT INTO `song` VALUES (16, '青花瓷', 9, '青花瓷 - 周杰伦 (Jay Chou)\r\n\r\n词：方文山\r\n\r\n曲：周杰伦\r\n\r\n编曲：钟兴民\r\n\r\n素胚勾勒出青花笔锋浓转淡\r\n\r\n瓶身描绘的牡丹一如你初妆\r\n\r\n冉冉檀香透过窗心事我了然\r\n\r\n宣纸上走笔至此搁一半\r\n\r\n釉色渲染仕女图韵味被私藏\r\n\r\n而你嫣然的一笑如含苞待放\r\n\r\n你的美一缕飘散\r\n\r\n去到我去不了的地方\r\n\r\n天青色等烟雨 而我在等你\r\n\r\n炊烟袅袅升起 隔江千万里\r\n\r\n在瓶底书汉隶仿前朝的飘逸\r\n\r\n就当我为遇见你伏笔\r\n\r\n天青色等烟雨 而我在等你\r\n\r\n月色被打捞起 晕开了结局\r\n\r\n如传世的青花瓷自顾自美丽\r\n\r\n你眼带笑意\r\n\r\n色白花青的锦鲤跃然于碗底\r\n\r\n临摹宋体落款时却惦记着你\r\n\r\n你隐藏在窑烧里千年的秘密\r\n\r\n极细腻犹如绣花针落地\r\n\r\n帘外芭蕉惹骤雨门环惹铜绿\r\n\r\n而我路过那江南小镇惹了你\r\n\r\n在泼墨山水画里\r\n\r\n你从墨色深处被隐去\r\n\r\n天青色等烟雨 而我在等你\r\n\r\n炊烟袅袅升起 隔江千万里\r\n\r\n在瓶底书汉隶仿前朝的飘逸\r\n\r\n就当我为遇见你伏笔\r\n\r\n天青色等烟雨 而我在等你\r\n\r\n月色被打捞起 晕开了结局\r\n\r\n如传世的青花瓷自顾自美丽\r\n\r\n你眼带笑意\r\n\r\n天青色等烟雨 而我在等你\r\n\r\n炊烟袅袅升起 隔江千万里\r\n\r\n在瓶底书汉隶仿前朝的飘逸\r\n\r\n就当我为遇见你伏笔\r\n\r\n天青色等烟雨 而我在等你\r\n\r\n月色被打捞起 晕开了结局\r\n\r\n如传世的青花瓷自顾自美丽\r\n\r\n你眼带笑意', 'https://y.gtimg.cn/music/photo_new/T002R300x300M000002eFUFm2XYZ7z.jpg?max_age=2592000', NULL, 3, NULL, NULL, NULL, '港台', '周杰伦', '03：59', NULL);
INSERT INTO `song` VALUES (17, '简单爱', 9, '简单爱 - 周杰伦 (Jay Chou)\r\n\r\n词：徐若瑄\r\n\r\n曲：周杰伦\r\n\r\n编曲：林迈可\r\n\r\n说不上为什么 我变得很主动\r\n\r\n若爱上一个人 什么都会值得去做\r\n\r\n我想大声宣布 对你依依不舍\r\n\r\n连隔壁邻居都猜到我现在的感受\r\n\r\n河边的风 在吹着头发 飘动\r\n\r\n牵着你的手 一阵莫名感动\r\n\r\n我想带你 回我的外婆家\r\n\r\n一起看着日落 一直到我们都睡着\r\n\r\n我想就这样牵着你的手不放开\r\n\r\n爱能不能够永远单纯没有悲哀\r\n\r\n我 想带你骑单车\r\n\r\n我 想和你看棒球\r\n\r\n想这样没担忧\r\n\r\n唱着歌 一直走\r\n\r\n我想就这样牵着你的手不放开\r\n\r\n爱可不可以简简单单没有伤害\r\n\r\n你 靠着我的肩膀\r\n\r\n你 在我胸口睡着\r\n\r\n像这样的生活\r\n\r\n我爱你 你爱我\r\n\r\n想 简简单单 爱\r\n\r\n想 简简单单 爱\r\n\r\n我想大声宣布 对你依依不舍\r\n\r\n连隔壁邻居都猜到我现在的感受\r\n\r\n河边的风 在吹着头发 飘动\r\n\r\n牵着你的手 一阵莫名感动\r\n\r\n我想带你 回我的外婆家\r\n\r\n一起看着日落 一直到我们都睡着\r\n\r\n我想就这样牵着你的手不放开\r\n\r\n爱能不能够永远单纯没有悲哀\r\n\r\n我 想带你骑单车\r\n\r\n我 想和你看棒球\r\n\r\n想这样没担忧\r\n\r\n唱着歌 一直走\r\n\r\n我想就这样牵着你的手不放开\r\n\r\n爱可不可以简简单单没有伤害\r\n\r\n你 靠着我的肩膀\r\n\r\n你 在我胸口睡着\r\n\r\n像这样的生活\r\n\r\n我爱你 你爱我\r\n\r\n想 简简单单 爱\r\n\r\n想 简简单单 爱\r\n\r\n我想就这样牵着你的手不放开\r\n\r\n爱能不能够永远单纯没有悲哀\r\n\r\n我 想带你骑单车\r\n\r\n我 想和你看棒球\r\n\r\n想这样没担忧\r\n\r\n唱着歌 一直走\r\n\r\n我想就这样牵着你的手不放开\r\n\r\n爱可不可以简简单单没有伤害\r\n\r\n你 靠着我的肩膀\r\n\r\n你 在我胸口睡着\r\n\r\n像这样的生活\r\n\r\n我爱你 你爱我', 'https://y.gtimg.cn/music/photo_new/T002R300x300M000000I5jJB3blWeN.jpg?max_age=2592000', NULL, 4, NULL, NULL, NULL, '港台', '周杰伦', '04：31', NULL);
INSERT INTO `song` VALUES (18, '七里香', 9, '七里香 - 周杰伦 (Jay Chou)\r\n\r\n词：方文山\r\n\r\n曲：周杰伦\r\n\r\n编曲：钟兴民\r\n\r\n窗外的麻雀在电线杆上多嘴\r\n\r\n你说这一句很有夏天的感觉\r\n\r\n手中的铅笔在纸上来来回回\r\n\r\n我用几行字形容你是我的谁\r\n\r\n秋刀鱼的滋味猫跟你都想了解\r\n\r\n初恋的香味就这样被我们寻回\r\n\r\n那温暖的阳光像刚摘的鲜艳草莓\r\n\r\n你说你舍不得吃掉这一种感觉\r\n\r\n雨下整夜我的爱溢出就像雨水\r\n\r\n院子落叶跟我的思念厚厚一叠\r\n\r\n几句是非也无法将我的热情冷却\r\n\r\n你出现在我诗的每一页\r\n\r\n雨下整夜我的爱溢出就像雨水\r\n\r\n窗台蝴蝶像诗里纷飞的美丽章节\r\n\r\n我接着写\r\n\r\n把永远爱你写进诗的结尾\r\n\r\n你是我唯一想要的了解\r\n\r\n雨下整夜我的爱溢出就像雨水\r\n\r\n院子落叶跟我的思念厚厚一叠\r\n\r\n几句是非也无法将我的热情冷却\r\n\r\n你出现在我诗的每一页\r\n\r\n那饱满的稻穗幸福了这个季节\r\n\r\n而你的脸颊像田里熟透的蕃茄\r\n\r\n你突然对我说七里香的名字很美\r\n\r\n我此刻却只想亲吻你倔强的嘴\r\n\r\n雨下整夜我的爱溢出就像雨水\r\n\r\n院子落叶跟我的思念厚厚一叠\r\n\r\n几句是非也无法将我的热情冷却\r\n\r\n你出现在我诗的每一页\r\n\r\n整夜我的爱溢出就像雨水\r\n\r\n窗台蝴蝶像诗里纷飞的美丽章节\r\n\r\n我接着写\r\n\r\n把永远爱你写进诗的结尾\r\n\r\n你是我唯一想要的了解', 'https://y.gtimg.cn/music/photo_new/T002R300x300M000003DFRzD192KKD.jpg?max_age=2592000', NULL, 4, NULL, NULL, NULL, '港台', '周杰伦', '04：59', NULL);
